type ReceiptLogsRlc struct {
	api             frontend.API
	maxLogPrefixLen int                 // maximum prefix length of rlp
	maxLogsRlpLen   int                 // maximum logs rlp len with prefix
	maxPerLogLen    int                 // maximum length of per log rlp
	maxLogs         int                 // maximum logs  number
	logsRlp         []frontend.Variable // decoded in-circuit from receipt rlp
	logsHexLen      frontend.Variable   // decoded in-circuit form receipt rlp
}

type logCircuit struct {
	LogRlp       []frontend.Variable
	LogRlpHexLen frontend.Variable
	DecodedLogs  [14][]frontend.Variable
}

func (c *logCircuit) Define(api frontend.API) error {
	receiptLogsRlc := &ReceiptLogsRlc{
		api:             api,
		maxLogPrefixLen: 8,
		maxLogsRlpLen:   6528,
		maxPerLogLen:    1024,
		maxLogs:         14,
		logsRlp:         c.LogRlp,
		logsHexLen:      c.LogRlpHexLen,
	}
	paddedLogs, err := receiptLogsRlc.RlcReceiptLogRlp()
	if err != nil {
		return err
	}

	for i := 0; i < len(paddedLogs); i++ {
		for j := 0; j < 1024; j++ {
			api.AssertIsEqual(paddedLogs[i][j], c.DecodedLogs[i][j])
		}
	}

	return nil
}

func bytesToHex(bt []byte) []frontend.Variable {
	var hex = make([]frontend.Variable, len(bt)*2)
	for i, b := range bt {
		n1 := b >> 4
		n2 := b & 0x0F

		hex[i*2] = n1
		hex[i*2+1] = n2
	}

	return hex
}

func TestLogDecode(t *testing.T) {
	assert := test.NewAssert(t)
	logRlp := ""

	log0Rlp := ""
	log1Rlp := ""
	log2Rlp := ""
	log3Rlp := ""
	log4Rlp := ""
	log5Rlp := ""
	log6Rlp := ""
	log7Rlp := ""
	log8Rlp := ""
	log9Rlp := ""
	log10Rlp := ""
	log11Rlp := ""
	log12Rlp := ""
	log13Rlp := ""

	logRlpBytes, err := hexutil.Decode(logRlp)
	assert.NoError(err)

	var circuit, assignment logCircuit
	circuit.LogRlp = make([]frontend.Variable, 6528)
	for i := 0; i < 14; i++ {
		circuit.DecodedLogs[i] = make([]frontend.Variable, 1024)
	}

	assignment.LogRlp = bytesToHex(logRlpBytes)
	assignment.LogRlpHexLen = big.NewInt(1966)
	assignment.DecodedLogs[0] = bytesToHex(hexutil.MustDecode(log0Rlp))
	assignment.DecodedLogs[1] = bytesToHex(hexutil.MustDecode(log1Rlp))
	assignment.DecodedLogs[2] = bytesToHex(hexutil.MustDecode(log2Rlp))
	assignment.DecodedLogs[3] = bytesToHex(hexutil.MustDecode(log3Rlp))
	assignment.DecodedLogs[4] = bytesToHex(hexutil.MustDecode(log4Rlp))
	assignment.DecodedLogs[5] = bytesToHex(hexutil.MustDecode(log5Rlp))
	assignment.DecodedLogs[6] = bytesToHex(hexutil.MustDecode(log6Rlp))
	assignment.DecodedLogs[7] = bytesToHex(hexutil.MustDecode(log7Rlp))
	assignment.DecodedLogs[8] = bytesToHex(hexutil.MustDecode(log8Rlp))
	assignment.DecodedLogs[9] = bytesToHex(hexutil.MustDecode(log9Rlp))
	assignment.DecodedLogs[10] = bytesToHex(hexutil.MustDecode(log10Rlp))
	assignment.DecodedLogs[11] = bytesToHex(hexutil.MustDecode(log11Rlp))
	assignment.DecodedLogs[12] = bytesToHex(hexutil.MustDecode(log12Rlp))
	assignment.DecodedLogs[13] = bytesToHex(hexutil.MustDecode(log13Rlp))

	err = test.IsSolved(&circuit, &assignment, ecc.BLS12_377.ScalarField())
	assert.NoError(err)

	circuitWitness, err := frontend.NewWitness(&assignment, ecc.BLS12_377.ScalarField())
	assert.NoError(err)

	pubW, err := circuitWitness.Public()
	assert.NoError(err)

	ccs, err := frontend.Compile(ecc.BLS12_377.ScalarField(), r1cs.NewBuilder, &circuit)
	assert.NoError(err)

	fmt.Println("ccs constraints:", ccs.GetNbConstraints())

	pk, vk, err := groth16.Setup(ccs)
	assert.NoError(err)

	nativeProver := regroth16.GetNativeProverOptions(ecc.BW6_761.ScalarField(), ecc.BLS12_377.ScalarField())
	solverOpts := solver.WithHints(DecodeReceiptLogs, GetLogRLPFromReceiptRLP)
	pf, err := groth16.Prove(ccs, pk, circuitWitness, nativeProver, backend.WithSolverOptions(solverOpts), backend.WithIcicleAcceleration())
	assert.NoError(err)

	nativeVerifierOptions := regroth16.GetNativeVerifierOptions(ecc.BW6_761.ScalarField(), ecc.BLS12_377.ScalarField())
	err = groth16.Verify(pf, vk, pubW, nativeVerifierOptions)
	assert.NoError(err)

}

func (c *ReceiptLogsRlc) RlcReceiptLogRlp() (paddedLogs [][]frontend.Variable, err error) {
	paddedLogs = make([][]frontend.Variable, c.maxLogs)
	hintOutLen := 1 + c.maxLogPrefixLen + c.maxLogsRlpLen
	api := c.api
	res, err := api.Compiler().NewHint(GetLogRLPFromReceiptRLP, hintOutLen, c.logsRlp...)

	// first value indicates the len of rlp prefix
	rlpPrefixLen := res[0]
	// logs rlp data including prefix
	paddedLogRlP := res[1:]
	// logs rlp prefix
	rlpPrefix := res[1 : 1+c.maxLogPrefixLen]
	// logs
	logsData := res[1+c.maxLogPrefixLen:]

	_, prefixOrTotalHexLen, isValid := RlpArrayPrefix(api, [2]frontend.Variable{c.logsRlp[0], c.logsRlp[1]})
	api.AssertIsEqual(isValid, 1)

	sub := NewSubArray(c.maxLogsRlpLen, c.maxLogPrefixLen, 3)
	prefix2, prefix2Len := sub.SubArray(api, c.logsRlp, 0, api.Add(2, rlpPrefixLen))
	prefixEq := ArrayEqual(api, rlpPrefix, prefix2, c.maxLogPrefixLen, prefix2Len)
	api.AssertIsEqual(prefixEq, 1)

	// constraint rlp decoded in hint (logsData) = logs rlp (rlpData2)
	rlpData2 := ShiftLeft(api, c.maxLogsRlpLen, 2, c.maxLogPrefixLen, c.logsRlp, api.Add(2, prefixOrTotalHexLen))
	logEqual := ArrayEqual(api, logsData, rlpData2, c.maxLogsRlpLen, c.logsHexLen)
	api.AssertIsEqual(logEqual, 1)

	// get logs rlp array in by hint
	var hintInputs []frontend.Variable
	var logInfo = [2]frontend.Variable{c.maxPerLogLen, c.maxLogs}
	hintInputs = append(hintInputs, logInfo[:]...)
	hintInputs = append(hintInputs, c.logsRlp[:]...)
	logsRlp, err := api.Compiler().NewHint(DecodeReceiptLogs, c.maxLogs+c.maxLogs*c.maxPerLogLen, hintInputs...)

	var logLens = make([]frontend.Variable, c.maxLogs)
	copy(logLens[:], logsRlp[:c.maxLogs])

	logs := logsRlp[c.maxLogs:]
	for i := 0; i < c.maxLogs; i++ {
		paddedLogs[i] = append(paddedLogs[i], logs[i*c.maxPerLogLen:(i+1)*c.maxPerLogLen]...)
	}

	// constraint the hint calculation results
	// rlc(prefix)*r^ len(logs) + rlc(logs) = rlc(logsRlp)
	multicommit.WithCommitment(api, func(api frontend.API, commitment frontend.Variable) error {
		// using paddedLogRlP calculated by hint works since it has been proved the `paddedLogRlP = logRlp` above
		commitment = 1
		coefficients, _ := RandLinearCoefficients(api, len(paddedLogRlP), commitment)
		rlcLogsRlp := RandLinearCombination(api, coefficients, paddedLogRlP)

		var logsRlcSegmentsNum = 1 + c.maxLogs

		var entryRLCs = make([]frontend.Variable, logsRlcSegmentsNum)
		var rlcSegmentLen = make([]frontend.Variable, logsRlcSegmentsNum)

		// logs prefix rlc
		prefixCoef := coefficients[0:c.maxLogPrefixLen]

		fmt.Println("rlpPrefix:", rlpPrefix)
		entryRLCs[0] = RandLinearCombination(api, prefixCoef, rlpPrefix)
		rlcSegmentLen[0] = c.maxLogPrefixLen

		for i := 0; i < c.maxLogs; i++ {
			logCoef := coefficients[0:c.maxPerLogLen]
			entryRLCs[i+1] = RandLinearCombination(api, logCoef, paddedLogs[i])
			rlcSegmentLen[i+1] = logLens[i]
		}

		fmt.Println("rlcSegmentLen:", rlcSegmentLen)

		var accRLC = entryRLCs[0]
		var accLen frontend.Variable = 0
		for i := 1; i < logsRlcSegmentsNum; i++ {
			accLen = api.Add(accLen, rlcSegmentLen[i-1])
			a_i := api.Mul(entryRLCs[i], Exp(api, commitment, accLen))
			accRLC = api.Add(accRLC, a_i)
			fmt.Println("a_i:", a_i)
		}
		api.AssertIsEqual(accRLC, rlcLogsRlp)
		api.Println(accRLC)
		api.Println(rlcLogsRlp)
		return nil
	}, paddedLogRlP...)

	return
}
